INSERT INTO `pn_agenda_invitado` (`id`, `id_agenda`, `hora`, `id_empleado`, `id_pn_subcapitulo`, `documentos`, `preguntas`, `resultados`) VALUES
(1, 2, 9, 'Presidente y Directivos de primer nivel', 1, 'Presentación general donde se indique la trayectoria de la empresa, su pensamiento organizacional y sus grandes retos a corto, mediano y largo plazo', '', NULL),
(2, 2, 9, 'Presidente y grupo ditrectivo', 1, 'Presentación de pensamiento organizacional y de principios y valores ', 'Cómo han difundido el pensamiento organizacional en las diferentes regionales y en otros países y cómo aseguran que se entiende y apropia?', NULL),
(3, 2, 9, 'Presidente y grupo directivo', 1, 'Los define la organización', '¿Cómo diferencian o relacionan la estrategia del direccionamiento estratégico?\n¿Cuál ha sido la efectividad de los proyectos bajo la metodología kickoff? \n¿Cómo se asegura que la estrategia está orientada al desarrollo social y la responsabilidad social?\n¿Cuáles han sido las innovaciones más relevantes en el proceso de formulación estratégica? \n', NULL),
(4, 2, 9, 'Presidente y directivos', 1, 'La organización decide', '¿Cómo afrontar el reto multicultural a la luz de la actual cultura de Servientrega?\nSi la innovación se considera un pilar estratégico, ¿cuáles son las metas a alcanzar? ¿Cuáles son los recursos que asigna para la misma?\n¿Cómo es en la práctica el despliegue del direccionamiento estratégico?\nEn el círculo virtuoso de la competitividad se indica que un enfoque multisolución se orienta a buscar desarrollar soluciones integrales para agregar valor estratégico a las cadenas de abastecimiento: aclarar ¿cómo siendo multisolución uno de los  elementos internos del circulo virtual es a la vez un enfoque?\n¿Cómo  revisan si la metodología aplicada en la definición de la planeación estratégica, es la adecuada para evaluar,  formular y mejorarla?\nLas alianzas, tales los empresarios satelitales, ¿cómo contribuyen a la gestión estratégica y en que forma la hacen?', NULL),
(5, 2, 9, 'Presidente y Grupo Directivo', 2, 'Plan estratégico', 'En el círculo virtuoso de la competitividad se indica que un enfoque multisolución se orienta a buscar desarrollar soluciones integrales para agregar valor estratégico a las cadenas de abastecimiento: aclarar ¿cómo siendo multisolución uno de los  elementos internos del circulo virtual es a la vez un enfoque?\n¿Cómo opera el plan marcha verde y el programa voces de excelencia? ¿Cómo se evalúa su eficacia? ', NULL),
(6, 2, 9, 'Presidente y Grupo Directivo', 3, 'Plan estratégico', '¿Cómo es en la práctica el despliegue del direccionamiento estratégico?\n¿Cómo se ha manejado el despliegue internacional de la organización? ¿Cómo afrontar el reto multicultural a la luz de la actual cultura de Servientrega?\n¿Cuál es el objetivo de cada comité? \n', NULL),
(7, 2, 9, 'Presidente y grupo ditrectivo', 4, 'Plan Estratégico', 'Si la innovación se considera un pilar estratégico, ¿cuáles son las metas a alcanzar? ¿Cuáles son los recursos que asigna para la misma?', NULL),
(8, 2, 9, 'Dinamizador de cliente interno', 5, 'A criterio de la aorganización', '¿Se mide el perfil del hombre Servientrega? \n¿Cuáles son los resultados de la evaluación de líderes? ', NULL),
(9, 2, 9, 'Dinamizador de cliente interno', 6, 'A criterio de la organización', '¿Cuál es la conformación de los comités?\n¿Cómo es el compromiso y las acciones relacionadas con el respeto y preservación de los derechos humanos? \n¿Cómo se realiza el plan de sucesión y desarrollo del talento humano?\n', NULL),
(10, 2, 9, 'Dinamizador del cliente interno', 7, 'A criterio de la organización', '¿Cómo diseña e  implementa el gobierno corporativo y cómo han resuelto casos de desvío?\n¿Cómo está definida la estructura e implementación del Gobierno Corporativo? ¿Qué documentos lo soporta? \n', NULL),
