  `c_100` varchar(700) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `id_capitulo` (`id_capitulo`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=31 ;

